/**
 * uni api简化
 */
import { uniformNameForVariable } from "ol/style/expressions";
import { getPixelRatio } from "../system/platform";
export {
  toast,
  toastFail,
  toastSuccess,
  toastError,
  loading,
  hiddenloading,
  platform,
  toastHide,
  getLocation,
  rpxToPx
};

const pixelRatio = getPixelRatio()

function rpxToPx(rpxNumber:number) {
  // const screenWidth = uni.getSystemInfoSync().windowHeight
  // return (rpx * Number.parseInt(screenWidth)) / 750
  return uni.upx2px(rpxNumber)
}

function getLocation(){
	return new Promise((resolve,reject)=>{
			uni.getLocation({
				type:'wgs84',//gcj02
			    success: (res)=>{
					console.log(res)
					resolve(res)
			    },
				fail: (e)=>{
					reject(e)
				}
			})
	})
}

/**
 * Toast消息
 */
function toast(msg, duration = 3000) {
  uni.showToast({
    icon: "none",
    title: msg,
    duration: duration,
  });
}
/**隐藏 */
function toastHide() {
  uni.hideToast();
}
/**
 * 操作成功Toast消息
 */
function toastSuccess(msg, duration = 1000) {
  uni.showToast({
    icon: "success",
    title: msg,
    duration: duration,
  });
}
/**
 * 操作失败Toast消息
 */
function toastFail(msg, duration = 3000) {
  uni.showToast({
    image:
      "data:image/png;base64,",
    title: msg,
    duration: duration,
  });
}
/**
 * Toast常驻 错误提示
 */
function toastError(msg, duration = 60000) {
  uni.showToast({
    icon: "none",
    title: msg,
    duration: duration,
  });
}

/**
 * 加载
 * @param {Object} msg
 */
function loading(msg) {
  uni.showLoading({
    title: msg,
    mask: true,
  });
}

function hiddenloading(msg) {
  uni.hideLoading()
}
/**
 * 获取平台
 * @return {string} platform   android|ios|xxx
 */
function platform() {
  return uni.getSystemInfoSync().platform;
}

function windowHeight() {
  return uni.getSystemInfoSync().windowHeight;
}
function windowWidth() {
  return uni.getSystemInfoSync().windowHeight;
}



